    def isMatch(self, s: str, p: str) -> bool:
        if not s and not p:
            return True
        if not s or not p:
            return False
        if p[0] == '*':
            return self.isMatch(s, p[1:]) or self.isMatch(s, p[1:] + '.*')
        if p[0] == '?':
            return self.isMatch(s, p[1:])
        if p[0] == s[0]:
            return self.isMatch(s[1:], p[1:])
